create table Street(street_name varchar(35) primary key, between_street_1 varchar(35), between_street_2 varchar(35), area varchar(20))
create table ParkingBay(device_id int primary key, street_marker varchar(6), sign_details   varchar(50), street_id int, side_of_street int, street_name varchar(35) references Street (street_name))
create table ParkingTime(device_id int references ParkingBay (device_id), arrival_time timestamp not null, departure_time timestamp, duration bigint, in_violation boolean, primary key (device_id, arrival_time))